    const [promotions, setPromotions] = useState<promotion[]>([]);

    const url = 'http://localhost:3000/promotions';
    useEffect(() => {
        getPromotions();


        Events.scrollEvent.register('begin', function() {
            console.log("begin");
          });
       
          Events.scrollEvent.register('end', function() {
