import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PromotionTable from './promotionsTable';
import { promotion } from './types/promotion';
import { Link, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'


export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);

    const url = 'http://localhost:3000/promotions';
    useEffect(() => {
        getPromotions();



        scrollSpy.update();

    }, []);

    const getPromotions = async () => {
        try {
            const { data } = await axios.post<promotion[]>(url);
            setPromotions(data);
        }
        catch (error) {
        }
    }

    return (
        <Link activeClass="active" to="test1" spy={true} smooth={true} offset={50} duration={500} >
            <div  >
                <PromotionTable />
                {/* <PromotionTable promotions={promotions} /> */}
            </div>
        </Link>

    );
}
