import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PromotionTable from './promotionsTable';
import { promotion } from '../types/promotion';
import InfiniteScroll from 'react-infinite-scroller';
import moment from 'moment'
import { Grid } from '@material-ui/core';



export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);
    // const [pagination, setPagination] = useState<pagination>({ page: 0, limit: 20 });
    const limit = 20;

    const url = 'http://localhost:3000/promotion/page';
    // useEffect(() => {
    //     getPromotions(0);
    // }, []);

    const getPromotions = async (page: number) => {
        try {
            // setPagination(pagination.page=>page)
            debugger
            const { data } = await axios.post<promotion[]>(url, {
                page, limit
            });
            data.map((p) => {
                return p;
            })
            setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    const duplicatePromotion = async (page: number) => {
        try {
            // setPagination(pagination.page=>page)
            debugger
            const { data } = await axios.post<promotion[]>(url, {
                page, limit
            });
            data.map((p) => {
                return p;
            })
            setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    const updatePromotion = async (page: number) => {
        try {
            // setPagination(pagination.page=>page)
            debugger
            const { data } = await axios.post<promotion[]>(url, {
                page, limit
            });
            data.map((p) => {
                return p;
            })
            setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    const deletePromotions = async (promotions: promotion[]) => {
        try {
            // setPagination(pagination.page=>page)
            // debugger
            // const { data } = await axios.delete(url, promotions);
            // data.map((p) => {
            //     return p;
            // })
            // setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center"
        >
            <Grid item xs={10}>
                <div className="promotions-list">
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={getPromotions}
                        hasMore={true}
                        loader={<div className="loader" key={0}>Loading ...</div>}
                        useWindow={false}>
                        <PromotionTable promotions={promotions} onDelete={this.handleLanguage} />
                    </InfiniteScroll>
                </div>
            </Grid>
        </Grid>
    );
}
