import React, { useState, useEffect } from 'react';
import PromotionTable from './promotionsTable';
import { promotion } from '../types/promotion';
import InfiniteScroll from 'react-infinite-scroller';
import { Grid } from '@material-ui/core';
import CircularProgress from '@material-ui/core/CircularProgress';
import * as Api from '../api/apiManager';


export interface TableProps {
    tasks: any[];
    onDelete: any;
    onDuplicate: any;
    onEdit: any;
    onSave: any;
}

export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);
    const [promotionKeys, setPromotionKeys] = useState<string[]>([]);
    const [hasMoreData, setHasMoreData] = useState<boolean>(true);
    const limit = Number(process.env.REACT_APP_PAGE_LIMIT);
    const maxPage = Number(process.env.REACT_APP_MAX_PAGE);

    useEffect(() => {
        initializeData();
    }, []);

    const initializeData = async () => {
        await getPromotions(0);
    }

    const getPromotions = async (page: number) => {
        Api.getPromotions(page, limit)
            .then(({ data }) => {
                setPromotionsData(data, page);
            }).catch((err) => {
                console.log(err)
            });
    }

    const setPromotionsData = (data: promotion[], page: number) => {
        const hasMore: boolean = (data.length && data.length === limit) ? true : false;
        setHasMoreData(hasMore);
        const excessRows = page > maxPage ? data.length - 10 : 0;
        const newPromotions = [...promotions.slice(excessRows)];
        setPromotions(promotions => [...newPromotions, ...data]);
    }

    const deletePromotions = async (ids: string[]) => {
        setPromotions(promotions => [...promotions.filter(({ _id }) => ids.indexOf(_id) === -1)]);
        await Api.deletePromotions(ids);
    }

    const updatePromotion = async (promotion: promotion) => {
        await Api.updatePromotion(promotion);
    }

    const duplicatePromotion = async (promotion: promotion) => {
        Api.duplicatePromotion(promotion)
            .then(({ data }) => {
                const index = promotions.findIndex(p => p._id === promotion._id);
                setPromotions(promotions => [
                    ...promotions.slice(0, index),
                    data,
                    ...promotions.slice(index)
                ]);
            }).catch((err) => {
                console.log(err)
            });
    }

    const editPromotion = (promotion: any) => {
        setPromotions([...promotions.map((p) => {
            if (p._id === promotion._id) return promotion
            return p;
        })])
    }

    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center">
            <Grid item xs={10}>
                <div className="promotions-list">
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={getPromotions}
                        hasMore={hasMoreData}
                        initialLoad={false}
                        loader={<CircularProgress color="secondary" key={'load'} />}
                        useWindow={false}>
                        <PromotionTable tasks={promotions} onDelete={deletePromotions}
                            onSave={updatePromotion} onDuplicate={duplicatePromotion} onEdit={editPromotion} />
                    </InfiniteScroll>
                </div>
            </Grid>
        </Grid>
    );
}
