import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PromotionTable from './promotionsTable';
import { promotion } from '../types/promotion';
import InfiniteScroll from 'react-infinite-scroller';
import moment from 'moment'
import { Grid } from '@material-ui/core';
import CircularProgress from '@material-ui/core/CircularProgress';
// import { environment } from '../react-app-env.env';


export interface TableProps {
    promotionKeys: string[];
    promotions: any[];
    onDelete: any;
    onDuplicate: any;
    onEdit: any;
}

export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);
    const [promotionKeys, setPromotionKeys] = useState<string[]>([]);
    const [hasMoreData, setHasMoreData] = useState<boolean>(true);
    const limit = 20;
    const url = 'http://localhost:4000/promotion';
    const maxRows = limit * 3;

    useEffect(() => {
        initializeData();
    }, []);

    const initializeData = async () => {
        await getPromotionKeys();
        await getPromotions(0);
    }

    const getPromotionKeys = async () => {
        try {
            const { data } = await axios.get<string[]>(`${url}/keys`);
            setPromotionKeys(data);
        }
        catch (error) {
            console.log(error);
        }
    }

    const getPromotions = async (page: number) => {
        try {
            const { data } = await axios.post<any[]>(`${url}/page`, {
                page, limit
            });
            const hasMore: boolean = (data.length && data.length == limit) ? true : false;
            setHasMoreData(hasMore);
            const shift = maxRows - promotions.length
            const shiftArray = shift < data.length ? data.length - shift : 0;
            debugger
            // const newArr = [...promotions.slice(shiftArray)]
            setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    const deletePromotions = async (promotionsIds: string) => {
        try {
            await axios.delete<void>(url, { data: promotionsIds });
        }
        catch (error) {
            console.log(error);
        }
    }

    const updatePromotion = async (promotions: promotion[]) => {
        try {
            const { data } = await axios.post<promotion[]>(url, {
            });
            // setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    const createPromotion = async (promotion: promotion) => {
        try {
            await axios.post<promotion[]>(url, {
                promotion
            });
            // setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    const editPromotion = async (promotion: promotion) => {
        try {
            await axios.post<promotion[]>(url, {
                promotion
            });
            // setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }


    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center">
            <Grid item xs={10}>
                <div className="promotions-list">
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={getPromotions}
                        hasMore={hasMoreData}
                        initialLoad={false}
                        loader={<CircularProgress color="secondary" key={'load'} />}
                        useWindow={false}>
                        <PromotionTable promotionKeys={promotionKeys}
                            promotions={promotions} onDelete={deletePromotions}
                            onSave={updatePromotion} onDuplicate={createPromotion} onEdit={editPromotion} />
                    </InfiniteScroll>
                </div>
            </Grid>
        </Grid>
    );
}
