const authHeader = () => {
    debugger;
    const user = JSON.parse(sessionStorage.getItem('user') || "user");

    if (user && user.accessToken) {
        // for Node.js Express back-end
        return { 'x-access-token': user.accessToken };
    } else {
        return {};
    }
}


export {
    authHeader
};
