import React, { useState, useEffect } from 'react';
import axios, { AxiosResponse } from 'axios';
import PromotionTable from './promotionsTable';
import { promotion } from '../types/promotion';
import InfiniteScroll from 'react-infinite-scroller';
import { Grid } from '@material-ui/core';
import CircularProgress from '@material-ui/core/CircularProgress';

export interface promotionData {
    promotionKeys: string[];
    promotions: any[];
}

export interface TableProps {
    data: promotionData;
    onDelete: any;
    onDuplicate: any;
    onEdit: any;
    onSave: any;
}

export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);
    const [promotionKeys, setPromotionKeys] = useState<string[]>([]);
    const [hasMoreData, setHasMoreData] = useState<boolean>(true);
    const limit = 20;
    const url = process.env.REACT_APP_API_URL as string;
    const maxRows = limit * 3;

    useEffect(() => {
        debugger
        initializeData();
    }, []);

    const initializeData = async () => {
        await getPromotionKeys();
        await getPromotions(0);
    }

    const getPromotionKeys = async () => {
        axios.get<string[]>(`${url}/keys`).then(({ data }: AxiosResponse) => {
            setPromotionKeys(data);
        }).catch((err) => {
            console.log(err)
        });
    }

    const getPromotions = async (page: number) => {
        await axios.post<promotion[]>(`${url}/page`, {
            page, limit
        })
            .then(({ data }: AxiosResponse) => {
                initializePromotions(data);
            }).catch((err) => {
                console.log(err)
            })
    }

    const initializePromotions = (data: promotion[]) => {
        const hasMore: boolean = (data.length && data.length == limit) ? true : false;
        setHasMoreData(hasMore);
        const slice = maxRows - promotions.length;
        const excessRows = slice < data.length ? data.length - slice : 0;
        const newPromotions = [...promotions.slice(excessRows)];
        setPromotions(promotions => [...newPromotions, ...data]);
    }

    const deletePromotions = async (ids: string[]) => {
        axios.delete<any>(url, { data: ids })
            .then((response: AxiosResponse) => {
                setPromotions([...promotions.filter(({ _id }) => ids.indexOf(_id) > -1)]);
            }).catch((err) => {
                console.log(err)
            })
    }

    const updatePromotion = async (promotion: promotion) => {
        axios.post<promotion[]>(url, {
            promotion
        }).then((response: AxiosResponse) => {
        }).catch((err) => {
            console.log(err)
        });
    }

    const duplicatePromotion = async (promotion: promotion) => {
        await axios.post<promotion>(url, {
            promotion
        }).then(({ data }: AxiosResponse) => {
            const index = promotions.findIndex(p => p._id == promotion._id);
            setPromotions(promotions => [
                ...promotions.slice(0, index),
                data,
                ...promotions.slice(index)
            ]);
        }).catch((err) => {
            console.log(err)
        });
    }

    const editPromotion = (promotion: any) => {
        setPromotions([...promotions.map((p) => {
            if (p._id == promotion._id) return promotion
            return p;
        })])
    }

    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center">
            <Grid item xs={10}>
                <div className="promotions-list">
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={getPromotions}
                        hasMore={hasMoreData}
                        initialLoad={false}
                        loader={<CircularProgress color="secondary" key={'load'} />}
                        useWindow={false}>
                        <PromotionTable data={{ promotionKeys, promotions }} onDelete={deletePromotions}
                            onSave={updatePromotion} onDuplicate={duplicatePromotion} onEdit={editPromotion} />
                    </InfiniteScroll>
                </div>
            </Grid>
        </Grid>
    );
}
