import React, { useState, useEffect } from 'react';
import PromotionTable from './tasksTable';
import { task } from '../types/task';
import InfiniteScroll from 'react-infinite-scroller';
import { Grid, Button } from '@material-ui/core';
import CircularProgress from '@material-ui/core/CircularProgress';
import * as TaskApi from '../services/TaskService';


export interface TableProps {
    tasks: any[];
    onDelete: any;
    onDuplicate: any;
    onEdit: any;
    onSave: any;
}

export const PromotionsList = () => {

    const [tasks, setTasks] = useState<task[]>([]);


    useEffect(() => {
        initializeData();
    }, []);

    const initializeData = async () => {
        await getTasks();
    }

    const getTasks = async () => {
        TaskApi.getTasks()
            .then(({ data }) => {
                setTasks(promotions => [...data]);

            }).catch((err) => {
                console.log(err)
            });
    }

    const deleteTask = async (id: string) => {
        setTasks(promotions => [...promotions.filter(({ _id }) => _id !== id)]);
        await TaskApi.deleteTasks(id);
    }

    const updatePromotion = async (promotion: task) => {
        await TaskApi.updateTask(promotion);
    }

    const duplicatePromotion = async (task: task) => {
        TaskApi.createTask(task)
            .then(({ data }) => {
                const index = tasks.findIndex(p => p._id === task._id);
                setTasks(promotions => [
                    ...promotions.slice(0, index),
                    data,
                    ...promotions.slice(index)
                ]);
            }).catch((err) => {
                console.log(err)
            });
    }

    const editTask = (task: any) => {
        setTasks([...tasks.map((p) => {
            if (p._id === promotion._id) return promotion
            return p;
        })])
    }

    const create = (promotion: any) => {
        const newTask = { _id: '', createDate: '', email: '', phone: '', userName: '' };
        setTasks(promotions => [
            newTask,
            ...promotions
        ]);;
    }

    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center">
            <Grid item xs={10}>
                <div className="promotions-list">
                    <Button variant="contained" color="primary" onClick={create}>
                        Create task
            </Button>
                    <PromotionTable tasks={tasks} onDelete={deleteTask}
                        onSave={updatePromotion} onDuplicate={duplicatePromotion} onEdit={editTask} />
                </div>
            </Grid>
        </Grid>
    );
}
