import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { useHistory } from "react-router-dom";
import { createPromotions } from '../api/apiManager'
import CircularProgress from '@material-ui/core/CircularProgress';


const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
            textTransform: 'none'
        },
    },
}));

export const CreatePromotions = () => {

    const [isLoading, setIsLoading] = useState<boolean>(false);
    const history = useHistory();
    const classes = useStyles();

    const create = async () => {
        setIsLoading(true);
        createPromotions()
            .then((response: any) => {
                setIsLoading(false);
                history.push("/promotions")
            }).catch((err) => {
                console.log(err)
            });
    }

    return (
        <div className={classes.root}>
            <Button disabled={isLoading} variant="contained" color="primary" onClick={create}>
                Create promotions
            </Button>
            {isLoading && <div><CircularProgress color="primary" key={'load'} /></div>}
        </div>
    );
}
