import React from 'react';
import { promotion } from '../types/promotion';
import clsx from 'clsx';
import { createStyles, lighten, makeStyles, Theme } from '@material-ui/core/styles';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import DeleteIcon from '@material-ui/icons/Delete';
import { Table, TableContainer, TableBody, TableHead, TableCell, withStyles, TextField } from '@material-ui/core';
import Moment from 'react-moment';
import { TableProps } from './promotionsList';
import { PromotionsTableToolbar } from './promotionsTableToolbar';
import { ActionsMenu } from './actionsMenu';
import DoneTwoToneIcon from '@material-ui/icons/DoneTwoTone';



const PromotionsTableHead = ({ headCells }: { headCells: string[] }) => {
    return (
        <TableHead>
            <TableRow>
                <StyledTableCell>
                </StyledTableCell>
                {headCells.map((headCell) => (
                    <StyledTableCell
                        key={headCell}>
                        {headCell}
                    </StyledTableCell>
                ))}
                <StyledTableCell>
                    Actions
                </StyledTableCell>
            </TableRow>
        </TableHead>
    );
}

const StyledTableCell = withStyles((theme: Theme) =>
    createStyles({
        head: {
            backgroundColor: theme.palette.primary.main,
            color: theme.palette.common.white,
        },
        body: {
            fontSize: 14,
        },
    }),
)(TableCell);

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            width: '100%',
            alignItems: 'center',
            marginTop: theme.spacing(3)
        },
        paper: {
            width: '100%',
            marginBottom: theme.spacing(2),
        },
        table: {
            minWidth: 750,
        },
        visuallyHidden: {
            border: 0,
            clip: 'rect(0 0 0 0)',
            height: 1,
            margin: -1,
            overflow: 'hidden',
            padding: 0,
            position: 'absolute',
            top: 20,
            width: 1,
        },
    }),
);

const useTextStyles = makeStyles(theme => ({
    disabledInput: {
        color: theme.palette.text.primary,
    },
    input: {
        color: theme.palette.text.primary,
    }
}));

const PromotionsTable = (props: TableProps) => {

    const classes = useStyles();
    const textClasses = useTextStyles();
    const [selected, setSelected] = React.useState<string[]>([]);
    const [editable, setEditable] = React.useState<string>();
    const { promotions, promotionKeys } = props;

    const selectRow = (event: React.MouseEvent<unknown>, id: string) => {
        const selectedIndex = selected.indexOf(id);
        let newSelected: string[] = [];
        if (selectedIndex === -1) {
            newSelected = newSelected.concat(selected, id);
        } else if (selectedIndex === 0) {
            newSelected = newSelected.concat(selected.slice(1));
        } else if (selectedIndex === selected.length - 1) {
            newSelected = newSelected.concat(selected.slice(0, -1));
        } else if (selectedIndex > 0) {
            newSelected = newSelected.concat(
                selected.slice(0, selectedIndex),
                selected.slice(selectedIndex + 1),
            );
        }
        setSelected(newSelected);
    };

    const deletePromotions = () => {
        props.onDelete(selected);
        setSelected([]);
    }

    const deletePromotion = (data: any) => {
        props.onDelete([data._id]);
    }

    const duplicatePromotion = (data: any) => {
        props.onDuplicate(data);
    }

    const savePromotion = () => {
        props.onSave(editable);
        setEditable('');
    }

    const editPromotion = (value: string, key: string, row: any) => {
        debugger
        row[key] = value;
        props.onEdit(row);
    }

    const setRowEditable = (data: any) => {
        setEditable(data._id);
    }

    const isSelected = (id: string) => selected.indexOf(id) !== -1;
    const isEditable = (id: string) => editable == id;

    return (
        <div className={classes.root}>
            <Paper className={classes.paper} >
                <PromotionsTableToolbar numSelected={selected.length} onDelete={deletePromotions} />
                <TableContainer>
                    <Table
                        className={classes.table}
                        aria-labelledby="tableTitle"
                        size="medium"
                        aria-label="enhanced table"
                    >
                        <PromotionsTableHead headCells={promotionKeys} />
                        <TableBody>
                            {promotions
                                .map((row, index) => {
                                    const isItemSelected = isSelected(row._id);
                                    const isDisable = !isEditable(row._id);
                                    const labelId = `checkbox-${index}`;
                                    const rowId = `row-${index}`;
                                    return (
                                        <TableRow
                                            hover
                                            role="checkbox"
                                            aria-checked={isItemSelected}
                                            key={rowId}
                                            selected={isItemSelected}>
                                            <TableCell padding="checkbox">
                                                <Checkbox
                                                    onClick={(event) => selectRow(event, row._id)}
                                                    checked={isItemSelected}
                                                    inputProps={{ 'aria-labelledby': labelId }}
                                                />
                                            </TableCell>
                                            {promotionKeys.map((key, i) =>
                                                (<TableCell key={`row-${i}`}>
                                                    <TextField onChange={e => editPromotion(e.target.value, key, row)} value={row[key]} disabled={isDisable} InputProps={{
                                                        disableUnderline: isDisable,
                                                        classes: { disabled: textClasses.disabledInput }
                                                    }} />
                                                </TableCell>
                                                ))}
                                            <TableCell>{
                                                isDisable ? (< ActionsMenu promotion={row} onDelete={deletePromotion}
                                                    onDuplicate={duplicatePromotion} onEdit={setRowEditable}></ActionsMenu>) : (
                                                        <DoneTwoToneIcon onClick={savePromotion}></DoneTwoToneIcon>)}
                                            </TableCell>
                                        </TableRow>
                                    );
                                })}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </div >
    );
}

export default PromotionsTable;
