import React, { useState, useEffect } from 'react';
import PromotionTable from './promotionsTable';
import { task } from '../types/task';
import InfiniteScroll from 'react-infinite-scroller';
import { Grid, Button } from '@material-ui/core';
import CircularProgress from '@material-ui/core/CircularProgress';
import * as TaskApi from '../services/TaskService';


export interface TableProps {
    tasks: any[];
    onDelete: any;
    onDuplicate: any;
    onEdit: any;
    onSave: any;
}

export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<task[]>([]);


    useEffect(() => {
        initializeData();
    }, []);

    const initializeData = async () => {
        await getPromotions();
    }

    const getPromotions = async () => {
        TaskApi.getPromotions()
            .then(({ data }) => {
                setPromotions(promotions => [...data]);

            }).catch((err) => {
                console.log(err)
            });
    }

    const deletePromotions = async (ids: string[]) => {
        setPromotions(promotions => [...promotions.filter(({ _id }) => ids.indexOf(_id) === -1)]);
        await TaskApi.deletePromotions(ids);
    }

    const updatePromotion = async (promotion: task) => {
        await TaskApi.updatePromotion(promotion);
    }

    const duplicatePromotion = async (promotion: task) => {
        TaskApi.duplicatePromotion(promotion)
            .then(({ data }) => {
                const index = promotions.findIndex(p => p._id === promotion._id);
                setPromotions(promotions => [
                    ...promotions.slice(0, index),
                    data,
                    ...promotions.slice(index)
                ]);
            }).catch((err) => {
                console.log(err)
            });
    }

    const editPromotion = (promotion: any) => {
        const newTask = { _id: '', createDate: '', email: '', phone: '', userName: '' };
        const newTasks= [...promotions.unshift(newTask)];
        setPromotions(newTask)
    }

    const create = () => {
        setPromotions([...promotions.map((p) => {
            if (p._id === promotion._id) return promotion
            return p;
        })])
    }

    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center">
            <Grid item xs={10}>
                <div className="promotions-list">
                    <Button variant="contained" color="primary" onClick={create}>
                        Create task
            </Button>
                    <PromotionTable tasks={promotions} onDelete={deletePromotions}
                        onSave={updatePromotion} onDuplicate={duplicatePromotion} onEdit={editPromotion} />
                </div>
            </Grid>
        </Grid>
    );
}
