import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PromotionTable from './promotionsTable';
import { promotion } from '../types/promotion';
import InfiniteScroll from 'react-infinite-scroller';
import moment from 'moment'
import { pagination } from '../types/pagination';


export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);
    // const [pagination, setPagination] = useState<pagination>({ page: 0, limit: 20 });
    const limit = 20;

    const url = 'http://localhost:3000/promotion/page';
    useEffect(() => {
        getPromotions(0);
    }, []);

    const getPromotions = async (page: number) => {
        try {
            // setPagination(pagination.page=>page)
            debugger
            const { data } = await axios.post<promotion[]>(url, {
                page, limit
            });
            console.log("promotions");
            data.map((p) => {
                return p;
            })
            setPromotions(promotions => [...promotions, ...data]);
            console.log(promotions.length);
        }
        catch (error) {
        }
    }

    return (
        <Grid
      container
      spacing={0}
      direction="column"
      alignItems="center"
      justify="center"
      className="home-container"
    >
      <Grid item xs={12}>
        <h1>Welcome to moonactive!!</h1>
        <h4>press create to start</h4>
      </Grid>
      <Grid item xs={12}>
        <CreateButton />
      </Grid>
    </Grid>
      

    );
}
