import React from 'react';
import { promotion } from '../types/promotion';
import clsx from 'clsx';
import { createStyles, lighten, makeStyles, Theme } from '@material-ui/core/styles';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import DeleteIcon from '@material-ui/icons/Delete';
import { Table, TableContainer, TableBody, TableHead, TableCell, withStyles } from '@material-ui/core';
import Moment from 'react-moment';
import { TableProps } from './promotionsList';

const PromotionsTableHead = () => {
    return (
        <TableHead>
            <TableRow>
                <StyledTableCell>
                </StyledTableCell>
                <StyledTableCell key='Promotion name'>
                    Promotion name
                </StyledTableCell>
                <StyledTableCell key='Type'>
                    Type
                </StyledTableCell>
                <StyledTableCell key='Start date'>
                    Start Date
                </StyledTableCell>
                <StyledTableCell key='End date'>
                    End date
                </StyledTableCell>
                <StyledTableCell key='User Group'>
                    User Group
                </StyledTableCell>
            </TableRow>
        </TableHead>
    );
}

const StyledTableCell = withStyles((theme: Theme) =>
    createStyles({
        head: {
            backgroundColor: theme.palette.primary.main,
            color: theme.palette.common.white,
        },
        body: {
            fontSize: 14,
        },
    }),
)(TableCell);

const useToolbarStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            paddingLeft: theme.spacing(2),
            paddingRight: theme.spacing(1),
        },
        highlight:
            theme.palette.type === 'light'
                ? {
                    color: theme.palette.secondary.main,
                    backgroundColor: lighten(theme.palette.secondary.dark, 0.85),
                }
                : {
                    color: theme.palette.text.primary,
                    backgroundColor: theme.palette.secondary.dark,
                },
        title: {
            flex: '1 1 100%',
        },
    }),
);



const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            width: '100%',
            alignItems: 'center',
            marginTop: theme.spacing(3)
        },
        paper: {
            width: '100%',
            marginBottom: theme.spacing(2),
        },
        table: {
            minWidth: 750,
        },
        visuallyHidden: {
            border: 0,
            clip: 'rect(0 0 0 0)',
            height: 1,
            margin: -1,
            overflow: 'hidden',
            padding: 0,
            position: 'absolute',
            top: 20,
            width: 1,
        },
    }),
);

const PromotionsTable = (props: TableProps) => {

    const classes = useStyles();
    const [selected, setSelected] = React.useState<string[]>([]);
    const { promotions } = props;

    const selectRow = (event: React.MouseEvent<unknown>, id: string) => {
        const selectedIndex = selected.indexOf(id);
        let newSelected: string[] = [];
        if (selectedIndex === -1) {
            newSelected = newSelected.concat(selected, id);
        } else if (selectedIndex === 0) {
            newSelected = newSelected.concat(selected.slice(1));
        } else if (selectedIndex === selected.length - 1) {
            newSelected = newSelected.concat(selected.slice(0, -1));
        } else if (selectedIndex > 0) {
            newSelected = newSelected.concat(
                selected.slice(0, selectedIndex),
                selected.slice(selectedIndex + 1),
            );
        }
        setSelected(newSelected);
    };



    const deletePromotions = () => {
        props.onDelete(selected);
        setSelected([]);

    }

    const isSelected = (id: string) => selected.indexOf(id) !== -1;

    return (
        <div className={classes.root}>
            <Paper className={classes.paper} >
                <PromotionsTableToolbar numSelected={selected.length} onDelete={deletePromotions} />
                <TableContainer>
                    <Table
                        className={classes.table}
                        aria-labelledby="tableTitle"
                        size="medium"
                        aria-label="enhanced table"
                    >
                        <PromotionsTableHead />
                        <TableBody>
                            {promotions
                                .map((row, index) => {
                                    const isItemSelected = isSelected(row._id);
                                    const labelId = `enhanced-table-checkbox-${index}`;
                                    return (
                                        <TableRow
                                            hover
                                            onClick={(event) => selectRow(event, row._id)}
                                            role="checkbox"
                                            aria-checked={isItemSelected}
                                            key={index}
                                            selected={isItemSelected}
                                        >
                                            <TableCell padding="checkbox">
                                                <Checkbox
                                                    checked={isItemSelected}
                                                    inputProps={{ 'aria-labelledby': labelId }}
                                                />
                                            </TableCell>
                                            <TableCell component="th" id={labelId} scope="row">
                                                {row.promotionName}
                                            </TableCell>
                                            <TableCell align="left">{row.type}</TableCell>
                                            <TableCell align="left">{row.startDate}</TableCell>
                                            <TableCell align="left">{row.endDate}</TableCell>
                                            <TableCell align="left">{row.userGroupName}</TableCell>
                                        </TableRow>
                                    );
                                })}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </div>
    );
}

export default PromotionsTable;
