import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PromotionTable from './promotionsTable';
import { promotion } from '../types/promotion';
import InfiniteScroll from 'react-infinite-scroller';
import moment from 'moment'
import { Grid } from '@material-ui/core';

export interface TableProps {
    promotions: promotion[];
    onDelete: any;
    onDuplicate: any;
    onEdit: any;
}

export const PromotionsList = () => {

    const [promotions, setPromotions] = useState<promotion[]>([]);
    const [hasMoreData, setHasMoreData] = useState<boolean>(true);
    const limit = 20;
    const url = 'http://localhost:3000/promotion/page';


    const getPromotions = async (page: number) => {
        try {
            const { data } = await axios.post<promotion[]>(url, {
                page, limit
            });
            // const hasMore:boolean = (data.length && data.length == limit);
            // setHasMoreData(hasMore);
            setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    
    const deletePromotions = async (promotions: promotion[]) => {
        try {
            const { data } = await axios.post<promotion[]>(url, {
    
            });

            setPromotions(promotions => [...promotions, ...data]);
        }
        catch (error) {
            console.log(error);
        }
    }

    return (
        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
            justify="center"
        >
            <Grid item xs={10}>
                <div className="promotions-list">
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={getPromotions}
                        hasMore={hasMoreData}
                        loader={<div className="loader" key={0}>Loading ...</div>}
                        useWindow={false}>
                        <PromotionTable promotions={promotions} onDelete={deletePromotions} />
                    </InfiniteScroll>
                </div>
            </Grid>
        </Grid>
    );
}
