import axios, { AxiosResponse } from 'axios';
import { promotion } from '../types/promotion';

const ApiManager = () => {
    const url = process.env.REACT_APP_API_URL as string;

    const createPromotions = () => {
        return axios.post(`${url}/batch`);
    }

    const getPromotionKeys = () => {
        return axios.get<string[]>(`${url}/keys`);
    }

    const getPromotions = (page: number, limit: number) => {
        return axios.post<promotion[]>(`${url}/page`, {
            page, limit
        })
    }

    const duplicatePromotion = (promotion: promotion) => {
        return axios.post<promotion>(url, {
            promotion
        })
    }

    const deletePromotions = async (ids: string[]) => {
        axios.delete<any>(url, { data: ids })
            .then((response: AxiosResponse) => {
            }).catch((err) => {
                console.log(err)
            })
    }

    const updatePromotion = async (promotion: promotion) => {
        axios.post<promotion[]>(url, {
            promotion
        }).then((response: AxiosResponse) => {
        }).catch((err) => {
            console.log(err)
        });
    }
    
}
export default ApiManager
