import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { Button, Grid, Link, MenuItem } from '@material-ui/core';
import { user } from '../types/user';
// import * as Roles from "../types/roles";
import * as AuthApi from '../services/UserService';
import { useHistory } from 'react-router-dom';



const useStyles = makeStyles((theme) => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    form: {
        width: '100%', 
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));


export const Register = () => {


    const [user, setUser] = useState<user>({ email: '', password: '', role: '' });
    const roles = ["admin", "user"];
    const history = useHistory();


    const classes = useStyles();


    const register = () => {
        AuthApi.register(user)
            .then(({ data }) => {
                history.push("/login")

            }).catch((err) => {
                console.log(err)
            });
    }

    return (
        <Grid
    container
    spacing={0}
    direction="column"
    alignItems="center"
    justify="center"
    className="home-container"
  >
    <Grid item xs={12} className={classes.root}>
      <h1>Welcome to propit!!</h1>
      <h4>please login to start manage your tasks</h4>
    </Grid>
    <Grid item xs={12}>
      <Login />
    </Grid>
  </Grid>
    
    );
}
